INSERT INTO properties (id, host_id, location_id, title, slug, description, type, address_full, status, rating_avg) VALUES 
('158a9563-7de0-4032-bcee-7e7be74b178b', '7a298b7f-c915-460a-b836-8198f57af3f2', (SELECT id FROM locations WHERE city = 'Đà Lạt' LIMIT 1), 'Apartment Công ty Phạm Đà Lạt', 'apartment-cong-ty-pham-a-lat', 'Năm hết là nón hai khâu thì khâu. Vá xanh xanh đá tàu mây leo không. Độc tủ bốn trăng tô tôi đã.', 'apartment', '8603 Đặng Neck', 'active', 3.1273062233641857),
('2f317d0a-1769-46e0-8e76-48ece24f93e0', '4280a4fe-d15c-4219-b734-3078e826bd07', (SELECT id FROM locations WHERE city = 'Hà Nội' LIMIT 1), 'Resort Cty TNHH Trần Hà Nội', 'resort-cty-tnhh-tran-ha-noi', 'Đồng tui chín ờ biết thôi hóa quần đá mây. Mướn hóa là. Đánh áo tám mua mười tàu đạp ngọt đánh.', 'resort', '158 Đỗ Well', 'active', 3.9062598850127506),
('ef782c44-e05f-49e7-ae44-1146bc7b2cac', '6f81b4c4-9135-443b-a35b-8e2b1bca0400', (SELECT id FROM locations WHERE city = 'Đà Nẵng' LIMIT 1), 'Hotel Cty Hồ Đà Nẵng', 'hotel-cty-ho-a-nang', 'Nước ừ đá vàng vẽ tôi cái một. Mua ừ biết áo xuồng mượn phá. Thế vàng không một thì đâu.', 'hotel', '13002 Hồ Flat', 'active', 4.623070820952687),
('3edfa249-5599-4211-963d-9883712bdfcf', '94c7f4dd-aa9e-47ad-85c5-2bcfcffe5d91', (SELECT id FROM locations WHERE city = 'Hồ Chí Minh' LIMIT 1), 'Hotel Cửa hàng Hà Hồ Chí Minh', 'hotel-cua-hang-ha-ho-chi-minh', 'Thôi kim xe chết. Sáu tôi thuê tàu leo thôi bàn bè ghế. Đồng yêu nước may tàu xe gì.', 'hotel', '628 Đặng Dam', 'active', 4.51517421592643),
('2d2f7ad0-877e-4774-8969-6907f67ae806', '94c7f4dd-aa9e-47ad-85c5-2bcfcffe5d91', (SELECT id FROM locations WHERE city = 'Hà Nội' LIMIT 1), 'Villa Chi nhánh Lâm Hà Nội', 'villa-chi-nhanh-lam-ha-noi', 'Bảy xanh nước trời độc ba ghế thích á. Khâu vẽ chìm đang á máy xuồng gì ghét biết. Ngọt núi núi nha mướn bàn.', 'villa', '35232 Hiểu Vân Hills', 'active', 3.042695533537583),
('dec5f672-ef54-4b2c-b15a-4fdd71765b99', 'd9805e2a-5a2d-434e-a1de-e475f8116432', (SELECT id FROM locations WHERE city = 'Phú Quốc' LIMIT 1), 'Homestay Công ty Ngô Phú Quốc', 'homestay-cong-ty-ngo-phu-quoc', 'Ừ thôi đã trời khâu là xuồng chín. Làm mượn đập khoảng bơi em tàu cái. Đỏ nhà hóa nước phá trời một.', 'homestay', '2394 Đình Nhân Haven', 'active', 3.292530666268631),
('b5c743be-67da-4dfb-ae94-eb1802680c8b', 'fe65cab6-ca32-48ec-bdc3-23e828c5061d', (SELECT id FROM locations WHERE city = 'Hội An' LIMIT 1), 'Hotel Cửa hàng Tô Hội An', 'hotel-cua-hang-to-hoi-an', 'Xe hai nước. Việc thuê tàu đâu yêu á hàng áo xanh quê. Chỉ nghỉ vàng trời quần tô sáu.', 'hotel', '99558 Vương Key', 'active', 4.640695102278172),
('f432bf78-555c-4128-a087-db1c92d42446', 'e8be4f75-1593-4d36-8226-2384b9e1c68a', (SELECT id FROM locations WHERE city = 'Đà Lạt' LIMIT 1), 'Apartment Trung tâm Lâm Đà Lạt', 'apartment-trung-tam-lam-a-lat', 'Ghét đập mười bơi may. Chìm nước chỉ viết. Tui hàng lầu ờ là ngọt vàng mượn được leo.', 'apartment', '11337 Hữu Trí Mission', 'active', 3.850092466293996),
('d32a07aa-d1aa-4973-836f-054f5b0b7261', '94c7f4dd-aa9e-47ad-85c5-2bcfcffe5d91', (SELECT id FROM locations WHERE city = 'Phú Quốc' LIMIT 1), 'Villa Cửa hàng Lê Phú Quốc', 'villa-cua-hang-le-phu-quoc', 'Việc nghỉ bàn. Dép thì ừ ghế làm ghế xanh ruộng. Biết khoảng đồng em viết.', 'villa', '903 Hiền Thục Common', 'active', 4.862535049749838),
('5ccc303a-2bbc-48df-9a05-d3a3a7cb1f5a', '4280a4fe-d15c-4219-b734-3078e826bd07', (SELECT id FROM locations WHERE city = 'Hà Nội' LIMIT 1), 'Homestay Chi nhánh Hà Hà Nội', 'homestay-chi-nhanh-ha-ha-noi', 'Ngọt xanh nước biển. Biết đâu mây mướn biết ghế ba. Làm hết giày hương.', 'homestay', '483 Lê Harbors', 'active', 3.4379882956895074),
('29d3c753-9714-4f94-a8b8-282589920e36', '7a298b7f-c915-460a-b836-8198f57af3f2', (SELECT id FROM locations WHERE city = 'Phú Quốc' LIMIT 1), 'Homestay Cửa hàng Vũ Phú Quốc', 'homestay-cua-hang-vu-phu-quoc', 'Nón khoảng may thế nghỉ biển. Đâu nước năm thích tàu. Máy gió khoảng.', 'homestay', '142 Trịnh Centers', 'active', 3.217106230537805),
('9c3c59fb-e231-4ddd-a987-bda11e492aa9', 'd9805e2a-5a2d-434e-a1de-e475f8116432', (SELECT id FROM locations WHERE city = 'Sapa' LIMIT 1), 'Homestay Cty Đào Sapa', 'homestay-cty-ao-sapa', 'Ruộng leo bốn thuê đỏ tím thế ờ dép. Bốn một thuê cửa tàu đang. Bốn bè cái một tui tím đang.', 'homestay', '4932 Hồng Khuê Ferry', 'active', 3.5215697521211426),
('b3e836a3-8305-489e-a364-01ef32393bfc', 'eb4ce09a-fb93-401a-be6f-f61a01c2a2a5', (SELECT id FROM locations WHERE city = 'Đà Nẵng' LIMIT 1), 'Resort Cty Đặng Đà Nẵng', 'resort-cty-ang-a-nang', 'Một tô nước khoan áo hóa bạn là cái. Mướn trời xanh cái vẽ nghỉ á may đánh. Vá bạn thương chỉ vẽ tàu tô thuê.', 'resort', '294 Hoàng Duệ Flats', 'active', 4.381131009910385),
('a81caf80-73c8-4908-ab15-28c69adce1b4', '6f81b4c4-9135-443b-a35b-8e2b1bca0400', (SELECT id FROM locations WHERE city = 'Hồ Chí Minh' LIMIT 1), 'Homestay Cty Trần Hồ Chí Minh', 'homestay-cty-tran-ho-chi-minh', 'Trời chín cửa ba trăng đạp em là giày ác. Sáu thì khoan gì tàu cái cái mây ác. Hương đá mười.', 'homestay', '725 Nam Sơn Station', 'active', 4.14370354714997),
('e5fa2ef3-6850-4298-a9c0-abf129d5d571', '4280a4fe-d15c-4219-b734-3078e826bd07', (SELECT id FROM locations WHERE city = 'Hồ Chí Minh' LIMIT 1), 'Hotel Cty Phạm Hồ Chí Minh', 'hotel-cty-pham-ho-chi-minh', 'Đã khoan chìm. Bảy cửa bàn kim là. Ruộng leo đập thuyền thuê tím biển bè trời.', 'hotel', '2848 Công Hiếu Keys', 'active', 4.217958376016962);
